SELECT B.USER_ID, B.NICKNAME, CONCAT(CONCAT(CONCAT(B.CITY, ' '), CONCAT(B.STREET_ADDRESS1,' ')), B.STREET_ADDRESS2) AS 전체주소, CONCAT(CONCAT(CONCAT(SUBSTR(TLNO,1,3),'-'),CONCAT(SUBSTR(TLNO,4,4),'-')),(SUBSTR(TLNO,8,4))) AS 전화번호 FROM USED_GOODS_USER B, (SELECT COUNT(WRITER_ID) AS CNT, WRITER_ID FROM USED_GOODS_BOARD GROUP BY WRITER_ID) AA WHERE AA.WRITER_ID = B.USER_ID AND CNT > 2 ORDER BY B.USER_ID DESC;